import React from 'react'

const AppControlsInput = ({addMealsHandler, calories, mealName, setCalo}) => {

     //when 'Enter' button is clicked, addMealsHandler will be called
  const onAddMealsClick = () =>{
    addMealsHandler();
 };

 //onChange, a function will be excecuted
  return (
    <form>

     <h1>Let's add a meal!</h1>
    <p>Meal</p>
    <input type ="text" placeholder ="meal" value={mealName}/>
    <p>Calorie Amount</p>
    <input type ="number" placeholder ="calories" value={calories}/>
    <button onClick ={onAddMealsClick} type="submit"><span></span>Enter</button>

    </form>
  );
};

export default AppControlsInput;
